"""Module having end points related to authentication."""import loggingfrom flask import gfrom flask.views import MethodViewfrom flask_smorest import Blueprintfrom src.schemas.auth_schema import LoginRequestSchema, LoginResponseSchemafrom src.helpers.log_helper import get_request_idfrom src.controller.auth_controller.login_controller import LoginControllerlogger = logging.getLogger(__name__)blp = Blueprint("authentication", __name__, description ="Authentication related operations")@blp.route("/v1/login")class Login(MethodView):    """        Class containing various methods applicable to /v1/login route.        ...        Methods        -------        POST    """    @blp.arguments(LoginRequestSchema)    @blp.response(200, LoginResponseSchema)    def post(self, credentials: dict) -> dict:        """            Method for login into system.            ...            On Success -> Follows LoginResponseSchema and returns access and refresh tokens.            On Failure -> Returns success = False and error message.        """        logger.info(f"[{get_request_id()}] : Login route accessed by user.")        return LoginController().user_login(credentials)